import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const FishDetail = (props) => {
    // 
    const [fish, setFish] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [name, setName] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        axios.get(`http://acnhapi.com/v1/fish/${id}`)
            .then(response => {
                // console.log(response.data)
                // console.log(response.data.availability)
                // console.log(response.data.name)

                setFish(response.data)
                setAvailability(response.data.availability)
                setName(response.data.name["name-USen"].charAt(0).toUpperCase() + response.data.name["name-USen"].slice(1))
            })
            .catch(err => {
                console.log(err);
                setFish("")
                //not successful
            })
    }, [id]);

    return (
        <div className="container-fluid p-5 bd-highlight d-inline-block mx-auto" style={{ backgroundImage: `url("https://www.videogamer.com/wp-content/uploads/3e0f20e8-6ca1-41b8-a7de-ef679905774d_animal-crossing-new-horizons-screenshot-january-1-raining-river.jpg")`, height: "800px", backgroundPosition: "center", borderRadius: "5%", backgroundAttachment: "fixed", backgroundRepeat: "no-repeat", backgroundSize: "cover", position: "relative", }}>
            <div className="card-header border-dark p-3 border border-dark d-flex p-3" style={{ display: "flex", borderRadius: "5% 5%", width: "700px", height: "400px", margin: "30px", padding: "20px", backgroundColor: "#66deff", border: "2px solid #eb8007", position: "fixed", margin: "100px", marginLeft: "300px" }}>
                <div>
                    <h1 style={{ border: "3px double #eb0f07", marginTop: "30px", transform: "rotate(-10deg)", backgroundColor: "#ff5768", color: "#FFEB3B", fontSize: "28px", textAlign: "center" }}>{name}</h1>
                    <img style={{ backgroundColor: "transparent" }} src={fish.image_uri} alt={fish["file-name"]} width="400" height="340"></img>
                </div>
                <div style={{ width: "350px", height: "300px", margin: "50px", padding: "20px",  border: "2px solid #eb8007", borderRadius: "25px", background: "#ffd857", opacity: "0.8" }}>
                    <div>
                        <h3>Price: {fish.price} bells </h3>
                        <p>({fish["price-flick"]} when to sell to Flick.)</p>

                        <div >
                            <h3>Rarity: {availability.rarity}</h3>
                        </div>
                        <div>
                            <h3>Location: {availability.location}</h3>
                        </div>
                        <div>
                            <h3>month-northern: {availability["month-northern"]}</h3>
                        </div>
                        <div>
                            <h3>month-southern: {availability["month-southern"]}</h3>
                        </div>
                    </div>
                </div>
            </div>
        </div>


    )
};

export default FishDetail;
