import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';

const Bugs = (props) => {

    const [bugs, setBugs] = useState([]);
    const [availability, setAvailability] = useState([]);

const {month} = props
const {hour} = props

    useEffect(() => {
        axios.get(`https://acnhapi.com/v1a/bugs/`)
            .then(response => {
                console.log(response.data);
                // console.log("this is the month array" + response.data[0].availability["month-array-northern"])
                // console.log("this is the time array" + response.data[0].availability["time-array"])
                setBugs(response.data)
                const result = bugs.filter(bug => bug.availability["month-array-northern"].includes(month) && bug.availability["time-array"].includes(hour))
                console.log(result)
                // setAvailability([...availability, response.data.availability])
                // setAvailability(response.data.availability)
            })
            .catch(error => {
                console.log(error)
            })
    }, []);

// console.log("this is the month" + month)
// console.log("this is the hour" + hour )


    return (
        <div>

{/* {JSON.stringify(Object.entries(bugs))} */}
            <h1>Bugs</h1>

            {bugs ?
            
            // bugs.filter(bug => bug[0].availability?.monthArrayNorthern.includes(month))
            bugs.map((bug, idx) => {
                return (
                    <>
                    <img key={bug.id} style={{width: "15%"}}src={`https://acnhapi.com/v1/images/bugs/${bug.id}`} />
                    </>
                )
            })

                // bugs.filter(bug => (bug.availability.monthArrayNorthern).includes(month)).map((bug, idx) => {
                //     return (
                //         <>
                //             <img key={bug.id} style={{ width: "75px" }} src={`https://acnhapi.com/v1/images/bugs/${bug.id}`} />
                //         </>
                //     )
                // })
                :
                <p>Loading..</p>}
                
        </div>)
};

export default Bugs;
