import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const SeaDetail = (props) => {

    const [sea, setSea] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [name, setName] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        axios.get(`http://acnhapi.com/v1/sea/${id}`)
            .then(response => {
                // console.log(response.data)
                // console.log(response.data.availability)

                setSea(response.data)
                setAvailability(response.data.availability)
                setName(response.data.name["name-USen"].charAt(0).toUpperCase() + response.data.name["name-USen"].slice(1))
            })
            .catch(err => {
                console.log(err);
                setSea("")
                //not successful
            })
    }, [id]);

    return (

        <div className="container-fluid p-5 bd-highlight d-inline-block mx-auto" style={{ backgroundImage: `url("https://static0.gamerantimages.com/wordpress/wp-content/uploads/2021/01/animal-crossing-new-horizons-giant-isopod-shown-off.jpg?q=50&fit=contain&w=960&h=500&dpr=1.5")`, height: "800px", backgroundPosition: "center", backgroundAttachment: "fixed", backgroundRepeat: "no-repeat", backgroundSize: "cover", position: "relative", }}>
            <div className="card-header border-dark p-3 border border-dark d-flex p-3" style={{ display: "flex", borderRadius: "5% 5%", width: "700px", height: "400px", margin: "30px", padding: "20px", backgroundColor: "#00ffff", border: "2px solid #40b830", position: "fixed", margin: "100px", marginLeft: "300px" }}>
                <div>
                    <h1 style={{ border: "3px double rebeccapurple", marginTop: "30px", transform: "rotate(-10deg)", background: "radial-gradient(circle, rgba(238,174,202,1) 0%, rgba(148,187,233,1) 100%)", color: "#FFEB3B", fontSize: "28px", textAlign: "center" }}>{name}</h1>
                    <img style={{ backgroundColor: "transparent" }} src={sea.image_uri} alt={sea["file-name"]} width="300" height="300"></img>

                </div>
                <div style={{ width: "300px", height: "300px", margin: "50px", padding: "20px", borderRadius: "25px", background: "#d987ff", opacity: "0.7" }}>
                    <div>
                        <h3>Price: {sea.price} bells</h3>
                        <p>({sea["price-flick"]} when to sell to Flick.)</p>
                    </div>
                    <div>
                        <h3>Rarity: {availability.rarity}</h3>

                    </div>
                    <div>
                        <h3>Location: {availability.location}</h3>
                    </div>
                    <div>
                        <h3>month-northern: {availability["month-northern"]}</h3>
                    </div>
                    <div>
                        <h3>month-southern: {availability["month-southern"]}</h3>
                    </div>
                </div>
            </div>
        </div >
    )
}

export default SeaDetail
