import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BugDetail = (props) => {
    const [bugs, setBugs] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [name, setName] = useState([]);
    const { id } = useParams();

    // axios uses response.data, fetch uses results
    useEffect(() => {
        axios.get(`http://acnhapi.com/v1a/bugs/${id}`)
            .then(response => {
                // console.log(response.data)
                // console.log(response.data.availability)
                // console.log(response.data.name)

                // foreach(response.data.availability["month-northern"])

                setBugs(response.data)
                setAvailability(response.data.availability)
                setName(response.data.name["name-USen"].charAt(0).toUpperCase() + response.data.name["name-USen"].slice(1))
            })
            .catch(err => {
                console.log(err);
                setBugs("")
                //not successful
            })
    }, [id]);
    // {bugID}



    return (
        <>
        <h1>Animal Crossing New Horizons </h1>
        <h1>Critter Finder</h1>
        // name, rarity, price, picture, location 
        <div style={{ backgroundImage: `url("https://pbs.twimg.com/media/ETF7pb8XkAEZcma.jpg")`, height: "800px", backgroundPosition: "center", backgroundAttachment: "fixed", backgroundRepeat: "no-repeat", backgroundSize: "cover", position: "relative", }}>
            <div style={{ display: "flex", borderRadius: "5% 5%", width: "700px", height: "400px", margin: "30px", padding: "20px", backgroundColor: "#ffa638", position: "fixed", margin: "100px", marginLeft: "300px" }}>
                {/* {JSON.stringify(bugs)} */}
                <div>
                    <h1 style={{ border: "3px double #e8ff38", width: "300px", marginTop: "20px", transform: "rotate(-10deg)", backgroundColor: "#5e2a10", color: "#FFEB3B", fontSize: "28px", textAlign: "center" }}>{name}</h1>

                    <img style={{ backgroundColor: "transparent", marginBottom: "20px", objectFit: "cover", objectPosition: "20% 10%" }} src={bugs.image_uri} alt={bugs["file-name"]} width="300" height="300"></img>
                </div>
                <div style={{ width: "300px", height: "300px", margin: "50px", padding: "20px", borderRadius: "25px", background: "#fa4141", opacity: "0.7" }}>
                    <div>
                        <h3>Price: {bugs.price} bells </h3>
                        <p>({bugs["price-flick"]} when to sell to Flick.)</p>
                    </div>
                    {/* {JSON.stringify(bugs.availability)} */}
                    <div >
                        <h3>Rarity: {availability.rarity}</h3>
                    </div>
                    <div>
                        <h3>Location: {availability.location}</h3>
                    </div>
                    <div>
                        <h3>month-northern: {availability["month-northern"]}</h3>
                    </div>
                    <div>
                        <h3>month-southern: {availability["month-southern"]}</h3>
                    </div>
                </div>
            </div>
        </div>
        </>
    )
};

export default BugDetail;

