import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';

const Bugs = (props) => {

    const [bugs, setBugs] = useState([]);
    const [available, setAvailable] = useState([]);

const {month} = props
const {hour} = props

    useEffect(() => {
        axios.get(`https://acnhapi.com/v1a/bugs/`)
            .then(response => {
                console.log(response.data);
                // console.log("this is the month array" + response.data[0].availability["month-array-northern"])
                // console.log("this is the time array" + response.data[0].availability["time-array"])
                setBugs(response.data)})
                .then ( () => {
                const result = bugs.filter(bug => bug.availability["month-array-northern"].includes(month) && bug.availability["time-array"].includes(hour))
                console.log("available" + result)
                setAvailable(result)
            })
            .catch(error => {
                console.log(error)
            })
    }, []);

// console.log("this is the month" + month)
console.log("this is the hour" + hour )


    return (
        <div>

            <h1>Bugs</h1>
{ 
bugs ? 
            available ?
        
            available.map((bug, idx) => {
                return (
                    <>
                    <img key={idx} style={{width: "75px"}} src={`https://acnhapi.com/v1/images/bugs/${bug.id}`} />
                    </>
                )
            })
                :
                <p>Loading..</p>
            
            : <p>Loading</p>}

                
        </div>)
};

export default Bugs
