import React, {useState, useEffect} from 'react';
import axios from 'axios';

const Fish = (props) => {
    const [fish, setFish] = useState([]);
    const [available, setAvailable] = useState([]);

    const { month, hour, isChanged, setIsChanged } = props

console.log("this is the month" + month)
console.log("hour " + hour )

    useEffect (() => {
        axios.get(`https://acnhapi.com/v1a/fish`)
        .then(response => {
            // console.log(response.data);
            setFish(response.data)
            const result = fish.filter(fish => fish.availability["month-array-northern"].includes(parseInt(month)) && fish.availability["time-array"].includes(parseInt(hour)))
            console.log(result)
            setAvailable(result)
            setIsChanged(true)
        })
        .catch(error => {
            console.log(error)
        })
}, [isChanged]);

    return (<div>
        {/* {JSON.stringify(seas)} */}
        <h1>Fish</h1>
            {available ?
                available.map((fish, idx) => {
                    return (
                            <img style={{width: "75px"}} key={fish.id} src={`https://acnhapi.com/v1/images/fish/${fish.id}`}/>
                    )
                })
            :
            <p>Loading..</p>}
    </div>)
};

export default Fish;
